def login_twitter
    # quelques lignes qui appellent les clés d'API de ton fichier .env
    client = Twitter::REST::Client.new do |config|
        config.consumer_key        = ENV["TWITTER_CONSUMER_KEY"]
        config.consumer_secret     = ENV["TWITTER_CONSUMER_SECRET"]
        config.access_token        = ENV["TWITTER_ACCESS_TOKEN"]
        config.access_token_secret = ENV["TWITTER_ACCESS_TOKEN_SECRET"]
    end
    client
end

def streaming_twitter
    client = Twitter::Streaming::Client.new do |config|
        config.consumer_key        = ENV["TWITTER_CONSUMER_KEY"]
        config.consumer_secret     = ENV["TWITTER_CONSUMER_SECRET"]
        config.access_token        = ENV["TWITTER_ACCESS_TOKEN"]
        config.access_token_secret = ENV["TWITTER_ACCESS_TOKEN_SECRET"]
    end
    client
end


# envoyer un tweet à 5 journalistes au hasard avec #bonjour_monde et @the_hacking_pro
def say_hello(array)
    
    # accounts_to_tweet = 5.times.map{ array[rand(0...array.length)] }.uniq
    accounts_to_tweet = array
    
    client = login_twitter
    accounts_to_tweet.each do |account|
        client.update("Salut #{account}! #bonjour_monde @the_hacking_pro")
    end

end

# liker les 25 derniers tweet avec un #bonjour_monde
# ne like pas les 25 plus récents mais les tweets du mois de "Mai"
def like_bonjour_monde
    client = login_twitter
    client.search("#bonjour_monde", result_type: "recent").take(25).collect do |tweet|
        client.fav tweet
    end
end

def follow_bonjour_monde
    client = login_twitter
    client.search("#bonjour_monde", result_type: "recent").take(5).collect do |tweet|
        client.follow tweet.user
    end
end

